Transaction.prototype.begin_ = function(callback) {
  var that = this;

  callback = callback || util.noop;

  this.makeReq_('beginTransaction', function(err, resp) {
    if (err) {
      callback(err, resp);
      return;
    }

    that.id = resp.transaction;

    callback(null, resp);
  });
};

