Transaction.prototype.save = function(entities) {
  var that = this;

  util.arrayize(entities).forEach(function(ent) {
    that.modifiedEntities_.push({
      entity: {
        key: ent.key
      },
      method: 'save',
      args: [ent]
    });
  });
};

