Topic.prototype.getSubscriptions = function(query, callback) {
  if (util.is(query, 'function')) {
    callback = query;
    query = {};
  }

  query.query = 'pubsub.googleapis.com/topic in (' + this.name + ')';

  this.pubsub.getSubscriptions(query, callback);
};

