IAM.prototype.setPolicy = function(policy, callback) {
  if (!is.object(policy)) {
    throw new Error('A policy object is required.');
  }

  this.request({
    method: 'POST',
    uri: ':setIamPolicy',
    json: {
      policy: policy
    }
  }, function(err, resp) {
    if (err) {
      callback(err, null, resp);
      return;
    }

    callback(null, resp, resp);
  });
};

